&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-yaml&#34; data-language=&#34;yaml&#34;&gt;&lt;code class=&#34;language-yaml&#34;&gt;---
version: &#34;2.1&#34;
services:
  calibre-web:
    image: linuxserver/calibre-web:latest
    container_name: calibre-web
    environment:
      - PUID=1000
      - PGID=1000
      - TZ=Europe/London
      - DOCKER_MODS=linuxserver/mods:universal-calibre #optional
      - OAUTHLIB_RELAX_TOKEN_SCOPE=1 #optional
    volumes:
      - /path/to/data:/config
      - /path/to/calibre/library:/books
    ports:
      - 8083:8083
    restart: unless-stopped&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;docker run -d \
  --name=calibre-web \
  -e PUID=1000 \
  -e PGID=1000 \
  -e TZ=Europe/London \
  -e DOCKER_MODS=linuxserver/mods:universal-calibre `#optional` \
  -e OAUTHLIB_RELAX_TOKEN_SCOPE=1 `#optional` \
  -p 8083:8083 \
  -v /path/to/data:/config \
  -v /path/to/calibre/library:/books \
  --restart unless-stopped \
  lscr.io/linuxserver/calibre-web:latest&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;p&gt;åŸä½œè€…ç²¾åŠ›ä¸å¤Ÿï¼Œä¸å†ç»´æŠ¤ä¸‹é¢è¿™ä¸ªç‰ˆæœ¬ï¼Œè¯·ä½¿ç”¨ linuxserver ç»´æŠ¤çš„ç‰ˆæœ¬ï¼&lt;/p&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-yaml&#34; data-language=&#34;yaml&#34;&gt;&lt;code class=&#34;language-yaml&#34;&gt;calibre:
  image: technosoft2000/calibre-web
  container_name: calibre
  hostname: calibre
  restart: always
  volumes:
    - ${USERDIR}/Calibre/web/config:/calibre-web/config
    - ${USERDIR}/Calibre/web/kindlegen:/calibre-web/kindlegen
    - ${USERDIR}/Calibre/web/app:/calibre-web/app
    - ${USERDIR}/Calibre/books:/books

  ports:
   - 8283:8083
  env_file:
    - .env
  environment:
    - PUID=${PUID}
    - PGID=${PGID}
    - TZ=${TZ}
    - USE_CONFIG_DIR=true
