&lt;h3 id=&#34;æ—¥å†åæ ‡ç³»Calendar&#34;&gt;æ—¥å†åæ ‡ç³»Calendar&lt;/h3&gt;
&lt;h4 id=&#34;Calendar-Heatmap-Horizontal&#34;&gt;&lt;a target=&#34;_blank&#34; rel=&#34;noopener&#34; href=&#34;https://echarts.apache.org/examples/zh/editor.html?c=calendar-horizontal&#34;&gt;Calendar Heatmap Horizontal&lt;/a&gt;&lt;/h4&gt;
&lt;script&gt;
function getVirtulData(year) {
    year = year || &#39;2017&#39;;
    var date = +echarts.number.parseDate(year + &#39;-01-01&#39;);
    var end = +echarts.number.parseDate((+year + 1) + &#39;-01-01&#39;);
    var dayTime = 3600 * 24 * 1000;
    var data = [];
    for (var time = date; time &lt; end; time += dayTime) {
        data.push([
            echarts.format.formatTime(&#39;yyyy-MM-dd&#39;, time),
            Math.floor(Math.random() * 1000)
        ]);
    }
    return data;
}
&lt;/script&gt;
