    &#34;ci&#34;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
    gulp.series(
        &#34;clean&#34;&lt;span class=&#34;token punctuation&#34;&gt;,&lt;/span&gt;
