&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 检测本机当前用户是否为超级管理员,如果是管理员,则使用 yum 安装 vsftpd,如果不  
# 是,则提示您非管理员(使用 UID 数字对比版本)  
if [ $UID -eq 0 ];then  
    yum ‐y install vsftpd  
else  
    echo &amp;quot;您不是管理员,没有权限安装软件&amp;quot;  
fi  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;9&#34;&gt;
&lt;li&gt;编写脚本：提示用户输入用户名和密码，脚本自动创建相应的账户及配置密码。如果用户不输入账户名，则提示必须输入账户名并退出脚本；如果用户不输入密码，则统一使用默认的 123456 作为默认密码。&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 编写脚本:提示用户输入用户名和密码,脚本自动创建相应的账户及配置密码。如果用户  
# 不输入账户名,则提示必须输入账户名并退出脚本;如果用户不输入密码,则统一使用默  
# 认的 123456 作为默认密码。  
read -p &amp;quot;请输入用户名: &amp;quot; user  
#使用‐z 可以判断一个变量是否为空,如果为空,提示用户必须输入账户名,并退出脚本,退出码为 2  
#没有输入用户名脚本退出后,使用$?查看的返回码为 2  
if [ -z $user ];then  
     echo &amp;quot;您不需输入账户名&amp;quot;  
   exit 2  
fi  
#使用 stty ‐echo 关闭 shell 的回显功能  
#使用 stty  echo 打开 shell 的回显功能  
stty -echo  
read -p &amp;quot;请输入密码: &amp;quot; pass  
stty echo  
pass&amp;#x3D;$&amp;#123;pass:‐123456&amp;#125;  
useradd &amp;quot;$user&amp;quot;  
echo &amp;quot;$pass&amp;quot; | passwd ‐‐stdin &amp;quot;$user&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;10&#34;&gt;
&lt;li&gt;输入三个数并进行升序排序&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 依次提示用户输入 3 个整数,脚本根据数字大小依次排序输出 3 个数字  
read -p &amp;quot;请输入一个整数:&amp;quot; num1  
read -p &amp;quot;请输入一个整数:&amp;quot; num2  
read -p &amp;quot;请输入一个整数:&amp;quot; num3  
# 不管谁大谁小,最后都打印 echo &amp;quot;$num1,$num2,$num3&amp;quot;  
# num1 中永远存最小的值,num2 中永远存中间值,num3 永远存最大值  
# 如果输入的不是这样的顺序,则改变数的存储顺序,如:可以将 num1 和 num2 的值对调  
tmp&amp;#x3D;0  
# 如果 num1 大于 num2,就把 num1 和和 num2 的值对调,确保 num1 变量中存的是最小值  
if [ $num1 -gt $num2 ];then     
  tmp&amp;#x3D;$num1  
  num1&amp;#x3D;$num2  
  num2&amp;#x3D;$tmp  
fi  
# 如果 num1 大于 num3,就把 num1 和 num3 对调,确保 num1 变量中存的是最小值  
if [ $num1 -gt $num3 ];then     
    tmp&amp;#x3D;$num1  
    num1&amp;#x3D;$num3  
    num3&amp;#x3D;$tmp  
fi  
# 如果 num2 大于 num3,就把 num2 和 num3 对标,确保 num2 变量中存的是小一点的值  
if [ $num2 -gt $num3 ];then  
    tmp&amp;#x3D;$num2  
    num2&amp;#x3D;$num3  
    num3&amp;#x3D;$tmp  
fi  
echo &amp;quot;排序后数据(从小到大)为:$num1,$num2,$num3&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;11&#34;&gt;
&lt;li&gt;石头、剪刀、布游戏&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 编写脚本,实现人机&amp;lt;石头,剪刀,布&amp;gt;游戏  
game&amp;#x3D;(石头 剪刀 布)  
num&amp;#x3D;$[RANDOM%3]  
computer&amp;#x3D;$&amp;#123;game[$num]&amp;#125;  
# 通过随机数获取计算机的出拳  
# 出拳的可能性保存在一个数组中,game[0],game[1],game[2]分别是 3 中不同的可能  

echo &amp;quot;请根据下列提示选择您的出拳手势&amp;quot;  
echo &amp;quot;1.石头&amp;quot;  
echo &amp;quot;2.剪刀&amp;quot;  
echo &amp;quot;3.布&amp;quot;  

read -p &amp;quot;请选择 1‐3:&amp;quot; person  
case  $person  in  
1)  
  if [ $num -eq 0 ]  
  then  
    echo &amp;quot;平局&amp;quot;  
    elif [ $num -eq 1 ]  
    then  
      echo &amp;quot;你赢&amp;quot;  
  else  
    echo &amp;quot;计算机赢&amp;quot;  
  fi;;  
2)     
  if [ $num -eq 0 ]  
  then  
    echo &amp;quot;计算机赢&amp;quot;  
    elif [ $num -eq 1 ]  
    then  
      echo &amp;quot;平局&amp;quot;  
  else  
    echo &amp;quot;你赢&amp;quot;  
  fi;;  
3)  
  if [ $num -eq 0 ]  
  then  
    echo &amp;quot;你赢&amp;quot;  
    elif [ $num -eq 1 ]  
    then  
      echo &amp;quot;计算机赢&amp;quot;  
  else  
    echo &amp;quot;平局&amp;quot;  
  fi;;  
*)  
  echo &amp;quot;必须输入 1‐3 的数字&amp;quot;  
esac  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
