killall $0  
echo &amp;quot;拷贝完成&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;16&#34;&gt;
&lt;li&gt;进度条,动态时针版本；定义一个显示进度的函数,屏幕快速显示| / ‐ \&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 进度条,动态时针版本  
# 定义一个显示进度的函数,屏幕快速显示|  &amp;#x2F; ‐ \  
rotate_line()&amp;#123;  
INTERVAL&amp;#x3D;0.5  #设置间隔时间  
COUNT&amp;#x3D;&amp;quot;0&amp;quot;     #设置4个形状的编号,默认编号为 0(不代表任何图像)  
while :  
do  
  COUNT&amp;#x3D;&amp;#96;expr $COUNT + 1&amp;#96; #执行循环,COUNT 每次循环加 1,(分别代表4种不同的形状)  
  case $COUNT in          #判断 COUNT 的值,值不一样显示的形状就不一样  
  &amp;quot;1&amp;quot;)                    #值为 1 显示‐  
          echo -e &amp;#39;‐&amp;#39;&amp;quot;\b\c&amp;quot;  
          sleep $INTERVAL  
          ;;  
    &amp;quot;2&amp;quot;)                  #值为 2 显示\\,第一个\是转义  
          echo -e &amp;#39;\\&amp;#39;&amp;quot;\b\c&amp;quot;  
          sleep $INTERVAL  
          ;;  
    &amp;quot;3&amp;quot;)                  #值为 3 显示|  
          echo -e &amp;quot;|\b\c&amp;quot;  
          sleep $INTERVAL  
          ;;  
   &amp;quot;4&amp;quot;)                   #值为 4 显示&amp;#x2F;  
          echo -e &amp;quot;&amp;#x2F;\b\c&amp;quot;  
          sleep $INTERVAL  
          ;;  
    *)                    #值为其他时,将 COUNT 重置为 0  
          COUNT&amp;#x3D;&amp;quot;0&amp;quot;;;  
    esac  
done  
&amp;#125;  
rotate_line  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
