# 9*9 乘法表(编写 shell 脚本,打印 9*9 乘法表)   
for i in &amp;#96;seq 9&amp;#96;  
do  
    for j in &amp;#96;seq $i&amp;#96;  
     do  
         echo -n &amp;quot;$j*$i&amp;#x3D;$[i*j]  &amp;quot;  
     done  
    echo  
