if [ ! ‐f &amp;#x2F;usr&amp;#x2F;lib&amp;#x2F;libmhash.so ];then  
  ln -s &amp;#x2F;usr&amp;#x2F;local&amp;#x2F;lib&amp;#x2F;libmhash.so &amp;#x2F;usr&amp;#x2F;lib&amp;#x2F;  
fi  
ldconfig  
else  
  echo &amp;quot;没有 mhash 源码包文件&amp;quot;  
  exit  
fi  
if [ -f libmcrypt‐2.5.8.tar.gz ];then  
  tar -xf libmcrypt‐2.5.8.tar.gz  
