# 对 100 以内的所有正整数相加求和(1+2+3+4...+100)  
#seq 100 可以快速自动生成 100 个整数  
sum&amp;#x3D;0  
for i in &amp;#96;seq 100&amp;#96;  
do  
    sum&amp;#x3D;$[sum+i]  
done  
echo &amp;quot;总和是:$sum&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
