host&amp;#x3D;192.168.51.198  
user&amp;#x3D;root  
passwd&amp;#x3D;123456  
mysqladmin -h &amp;#39;$host&amp;#39; -u &amp;#39;$user&amp;#39; -p&amp;#39;$passwd&amp;#39; ping &amp;amp;&amp;gt;&amp;#x2F;dev&amp;#x2F;null  
if [ $? -eq 0 ]  
then  
        echo &amp;quot;MySQL is UP&amp;quot;  
else  
        echo &amp;quot;MySQL is down&amp;quot;  
