read -p &amp;quot;请输入逻辑卷名称:&amp;quot; lv_name  
[ -z $lv_name ] &amp;amp;&amp;amp; echo &amp;quot;没有输入逻辑卷名称&amp;quot; &amp;amp;&amp;amp; exit  
read -p &amp;quot;请输入逻辑卷大小:&amp;quot; lv_size  
[ -z $lv_size ] &amp;amp;&amp;amp; echo &amp;quot;没有输入逻辑卷大小&amp;quot; &amp;amp;&amp;amp; exit  
# 使用命令创建逻辑卷  
pvcreate $disk  
vgcreate $vg_name $disk  
lvcreate -L $&amp;#123;lv_size&amp;#125;M -n $&amp;#123;lv_name&amp;#125;  $&amp;#123;vg_name&amp;#125;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
