do  
  awk -F: -v x&amp;#x3D;$i &amp;#39;$1&amp;#x3D;&amp;#x3D;x&amp;#123;print $1,$2&amp;#125;&amp;#39; &amp;#x2F;etc&amp;#x2F;shadow  
done  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;73&#34;&gt;
&lt;li&gt;统计/etc/passwd 中 root 出现的次数&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 统计&amp;#x2F;etc&amp;#x2F;passwd 中 root 出现的次数   

#每读取一行文件内容,即从第 1 列循环到最后 1 列,依次判断是否包含 root 关键词,如果包含则 x++  
awk -F: &amp;#39;&amp;#123;i&amp;#x3D;1;while(i&amp;lt;&amp;#x3D;NF)&amp;#123;if($i~&amp;#x2F;root&amp;#x2F;)&amp;#123;x++&amp;#125;;i++&amp;#125;&amp;#125; END&amp;#123;print &amp;quot;root 出现次数为&amp;quot;x&amp;#125;&amp;#39; &amp;#x2F;etc&amp;#x2F;passwd  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;74&#34;&gt;
&lt;li&gt;统计 Linux 进程相关数量信息&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 统计 Linux 进程相关数量信息   
running&amp;#x3D;0  
sleeping&amp;#x3D;0  
stoped&amp;#x3D;0  
zombie&amp;#x3D;0  
# 在 proc 目录下所有以数字开始的都是当前计算机正在运行的进程的进程 PID  
# 每个 PID 编号的目录下记录有该进程相关的信息  
for pid in &amp;#x2F;proc&amp;#x2F;[1‐9]*  
do  
  procs&amp;#x3D;$[procs+1]  
  stat&amp;#x3D;$(awk &amp;#39;&amp;#123;print $3&amp;#125;&amp;#39; $pid&amp;#x2F;stat)  
# 每个 pid 目录下都有一个 stat 文件,该文件的第 3 列是该进程的状态信息  
    case $stat in  
    R)  
    running&amp;#x3D;$[running+1]  
    ;;  
    T)  
    stoped&amp;#x3D;$[stoped+1]  
    ;;  
    S)  
    sleeping&amp;#x3D;$[sleeping+1]  
    ;;  
    Z)  
     zombie&amp;#x3D;$[zombie+1]  
     ;;  
    esac  
done  
echo &amp;quot;进程统计信息如下&amp;quot;  
echo &amp;quot;总进程数量为:$procs&amp;quot;  
echo &amp;quot;Running 进程数为:$running&amp;quot;  
echo &amp;quot;Stoped 进程数为:$stoped&amp;quot;  
echo &amp;quot;Sleeping 进程数为:$sleeping&amp;quot;  
echo &amp;quot;Zombie 进程数为:$zombie&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;75&#34;&gt;
&lt;li&gt;从键盘读取一个论坛积分,判断论坛用户等级&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 从键盘读取一个论坛积分,判断论坛用户等级  

#等级分类如下:  
#  大于等于 90        神功绝世  
#  大于等于 80,小于 90       登峰造极  
#  大于等于 70,小于 80       炉火纯青  
#  大于等于 60,小于 70       略有小成  
#  小于 60               初学乍练  
read -p &amp;quot;请输入积分(0‐100):&amp;quot; JF  
if [ $JF -ge 90 ] ; then  
  echo &amp;quot;$JF 分,神功绝世&amp;quot;  
elif [ $JF -ge 80 ] ; then  
    echo &amp;quot;$JF 分,登峰造极&amp;quot;  
elif [ $JF -ge 70 ] ; then  
    echo &amp;quot;$JF 分,炉火纯青&amp;quot;  
elif [ $JF -lt 60 ] ; then  
    echo &amp;quot;$JF 分,略有小成&amp;quot;  
else  
    echo &amp;quot;$JF 分,初学乍练&amp;quot;  
fi  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
