&lt;ol start=&#34;92&#34;&gt;
&lt;li&gt;使用 shell 脚本打印图形&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 使用 shell 脚本打印如下图形:   
# 打印第一组图片  
# for(())为类 C 语言的语法格式,也可以使用 for i  in;do  ;done 的格式替换  
# for((i&amp;#x3D;1;i&amp;lt;&amp;#x3D;9;i++))循环会执行 9 次,i 从 1 开始到 9,每循环一次 i 自加 1  
clear  
for (( i&amp;#x3D;1; i&amp;lt;&amp;#x3D;9; i++ ))  
do  
  for (( j&amp;#x3D;1; j&amp;lt;&amp;#x3D;i; j++ ))  
  do  
    echo -n &amp;quot;$i&amp;quot;  
  done  
  echo &amp;quot;&amp;quot;  
done  
read  -n1  &amp;quot;按任意键继续&amp;quot;  key  
#打印第二组图片  
clear  
for (( i&amp;#x3D;1; i&amp;lt;&amp;#x3D;5; i++ ))   
do  
  for (( j&amp;#x3D;1; j&amp;lt;&amp;#x3D;i; j++ ))  
  do  
    echo -n &amp;quot; |&amp;quot;  
  done  
  echo &amp;quot;_ &amp;quot;  
done  
read  -n1  &amp;quot;按任意键继续&amp;quot;  key  
#打印第三组图片  
clear  
for (( i&amp;#x3D;1; i&amp;lt;&amp;#x3D;5; i++ ))  
do  
  for (( j&amp;#x3D;1; j&amp;lt;&amp;#x3D;i; j++ ))  
  do  
    echo -n &amp;quot; *&amp;quot;  
  done  
  echo &amp;quot;&amp;quot;  
done  
for (( i&amp;#x3D;5; i&amp;gt;&amp;#x3D;1; i‐‐ ))  
do  
  for (( j&amp;#x3D;1; j&amp;lt;&amp;#x3D;i; j++ ))  
  do  
    echo -n &amp;quot; *&amp;quot;  
  done  
  echo &amp;quot;&amp;quot;  
done  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;93&#34;&gt;
&lt;li&gt;根据计算机当前时间,返回问候语,可以将该脚本设置为开机启动&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 根据计算机当前时间,返回问候语,可以将该脚本设置为开机启动   

# 00‐12 点为早晨,12‐18 点为下午,18‐24 点为晚上  
# 使用 date 命令获取时间后,if 判断时间的区间,确定问候语内容  
tm&amp;#x3D;$(date +%H)  
if [ $tm -le 12 ];then  
  msg&amp;#x3D;&amp;quot;Good Morning $USER&amp;quot;  
elif [ $tm -gt 12 -a $tm -le 18 ];then  
    msg&amp;#x3D;&amp;quot;Good Afternoon $USER&amp;quot;  
else  
    msg&amp;#x3D;&amp;quot;Good Night $USER&amp;quot;  
fi  
echo &amp;quot;当前时间是:$(date +&amp;quot;%Y‐%m‐%d %H:%M:%S&amp;quot;)&amp;quot;  
echo -e &amp;quot;\033[34m$msg\033[0m&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;94&#34;&gt;
&lt;li&gt;读取用户输入的账户名称,将账户名写入到数组保存&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 读取用户输入的账户名称,将账户名写入到数组保存   
# 定义数组名称为 name,数组的下标为 i,小标从 0 开始,每输入一个账户名,下标加 1,继续存下一个账户  
# 最后,输入 over,脚本输出总结性信息后脚本退出  
i&amp;#x3D;0  
while :  
do  
  read -p &amp;quot;请输入账户名,输入 over 结束:&amp;quot; key  
  if [ $key &amp;#x3D;&amp;#x3D; &amp;quot;over&amp;quot; ];then   
    break  
    else  
    name[$i]&amp;#x3D;$key  
    let i++  
    fi  
done  
echo &amp;quot;总账户名数量:$&amp;#123;#name[*]&amp;#125;&amp;quot;  
echo &amp;quot;$&amp;#123;name[@]&amp;#125;&amp;quot;  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;ol start=&#34;95&#34;&gt;
&lt;li&gt;判断文件或目录是否存在&lt;/li&gt;
&lt;/ol&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;#!&amp;#x2F;bin&amp;#x2F;bash  

# 判断文件或目录是否存在   
if [ $# -eq 0 ] ;then  
echo &amp;quot;未输入任何参数,请输入参数&amp;quot;  
echo &amp;quot;用法:$0 [文件名|目录名]&amp;quot;  
fi  
if [ -f $1 ];then  
  echo &amp;quot;该文件,存在&amp;quot;  
  ls -l $1  
else  
  echo &amp;quot;没有该文件&amp;quot;  
fi  
if [ -d  $1 ];then  
     echo &amp;quot;该目录,存在&amp;quot;  
     ls -ld  $2  
else  
     echo &amp;quot;没有该目录&amp;quot;  
fi  &lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
