      document.getElementById(&#34;mmedia-uKpQZTOYrtMOZMKW&#34;).style.height=document.getElementById(&#34;mmedia-uKpQZTOYrtMOZMKW&#34;).scrollWidth*0.76+&#34;px&#34;;
