&#34;show-filterbar&#34;: true,
&#34;show-notification-area-icon&#34;: false,
&#34;show-options-window&#34;: true,
&#34;show-statusbar&#34;: true,
&#34;show-toolbar&#34;: true,
&#34;show-tracker-scrapes&#34;: true,
