 &lt;h2 id=&#34;安装&#34;&gt;安装&lt;/h2&gt;
&lt;h3 id=&#34;qnapclub&#34;&gt;&lt;strong&gt;qnapclub&lt;/strong&gt;&lt;/h3&gt;
&lt;p&gt;&lt;img src=&#34;https://cimg1.17lai.site/data/2021/08/1820210818163248.png&#34; alt=&#34;&#34;&gt;&lt;/p&gt;
&lt;h3 id=&#34;docker&#34;&gt;&lt;strong&gt;docker&lt;/strong&gt;&lt;/h3&gt;
&lt;p&gt;&lt;a href=&#34;https://hub.docker.com/r/linuxserver/transmission&#34;&gt;docker hub&lt;/a&gt;&lt;/p&gt;
&lt;blockquote&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-yaml&#34; data-language=&#34;yaml&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-yaml&#34;&gt;services:
transmission:
   image: ghcr.io&amp;#x2F;linuxserver&amp;#x2F;transmission
    container_name: transmission
    environment:
         - PUID&amp;#x3D;1000
      - PGID&amp;#x3D;1000
      - TZ&amp;#x3D;Europe&amp;#x2F;London
      - TRANSMISSION_WEB_HOME&amp;#x3D;&amp;#x2F;combustion-release&amp;#x2F; #optional
      - USER&amp;#x3D;username #optional
      - PASS&amp;#x3D;password #optional
      - WHITELIST&amp;#x3D;iplist #optional
      - HOST_WHITELIST&amp;#x3D;dnsnane list #optional
    volumes:
      - &amp;lt;path to data&amp;gt;:&amp;#x2F;config
      - &amp;lt;path to downloads&amp;gt;:&amp;#x2F;downloads
      - &amp;lt;path to watch folder&amp;gt;:&amp;#x2F;watch
    ports:
      - 9091:9091
      - 51413:51413
      - 51413:51413&amp;#x2F;udp
    restart: unless-stopped&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;/blockquote&gt;
&lt;h2 id=&#34;控制脚本&#34;&gt;控制脚本&lt;/h2&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;&amp;#x2F;etc&amp;#x2F;init.d&amp;#x2F;QTransmission.sh start
&amp;#x2F;etc&amp;#x2F;init.d&amp;#x2F;QTransmission.sh stop&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;h2 id=&#34;Transmission-配置详解&#34;&gt;Transmission 配置详解&lt;/h2&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-bash&#34; data-language=&#34;bash&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-bash&#34;&gt;vi &amp;#x2F;share&amp;#x2F;CACHEDEV1_DATA&amp;#x2F;.qpkg&amp;#x2F;QTransmission&amp;#x2F;etc&amp;#x2F;settings.json&lt;/code&gt;&lt;/pre&gt;&lt;/div&gt;&lt;/figure&gt;
&lt;figure&gt;&lt;div class=&#34;code-area&#34;&gt;&lt;pre class=&#34;line-numbers language-json&#34; data-language=&#34;json&#34; data-start=&#34;1&#34;&gt;&lt;code class=&#34;language-json&#34;&gt;&amp;quot;alt-speed-up&amp;quot;: 500, # 限速时段上传限速值
&amp;quot;alt-speed-down&amp;quot;: 500, # 限速时段下载限速值
&amp;quot;alt-speed-enabled&amp;quot;: false,
&amp;quot;alt-speed-time-begin&amp;quot;: 540,
&amp;quot;alt-speed-time-day&amp;quot;: 127, # 时段限速日期（星期几），127 表示每天，更复杂配置参考官网。用 7 位二进制数表示，然后转换成十进制数，0000001 表示周日，1000000 表示周六，0000010 表示周一，0000100 表示周二。如果你只要在周末限速，该数应该 1000001，转换为十进制就是 65
&amp;quot;alt-speed-time-enabled&amp;quot;: true, # 启用限速，为 false 时，以上计划配置则不生效，生效时会自动禁用 alt-speed-enabled 配置，二者只能选一个
&amp;quot;alt-speed-time-end&amp;quot;: 420, # 限速时段结束时间，这个配置表示的是凌晨零点到开始时间的分钟数，比如 7:00 就是 7*60&amp;#x3D;420。需要注意的是，该时间是用的 GMT 时间，即北京时间 -8 小时。比如你计划北京时间 7 点 30 分开始，这个数字应该是（7-8+24）*60+30&amp;#x3D;1410
&amp;quot;bind-address-ipv4&amp;quot;: &amp;quot;0.0.0.0&amp;quot;, # IPv4 地址绑定，一般不要改动
&amp;quot;bind-address-ipv6&amp;quot;: &amp;quot;::&amp;quot;, #IPv6 地址绑定，一般不要改动
&amp;quot;blocklist-enabled&amp;quot;: true, # 启动白名单，默认不启动，需要启动改为 true
&amp;quot;blocklist-updates-enabled&amp;quot;: false,
&amp;quot;blocklist-url&amp;quot;: &amp;quot;http:&amp;#x2F;&amp;#x2F;www.example.com&amp;#x2F;blocklist&amp;quot;,
&amp;quot;cache-size-mb&amp;quot;: 4, #缓存大小，以 MB 为单位，建议设大一些，避免频繁读写硬盘而伤硬盘，建议设为内存大小的 1&amp;#x2F;6～1&amp;#x2F;4
&amp;quot;compact-view&amp;quot;: false,
&amp;quot;dht-enabled&amp;quot;: false, #关闭 DHT（不通过 tracker 寻找节点）功能，不少 PT 站的要求，但 BT 下载设置为 true 会使得下载更好
&amp;quot;download-dir&amp;quot;: &amp;quot;&amp;#x2F;share&amp;#x2F;Downloads&amp;quot;, #下载的内容存放的目录
&amp;quot;download-queue-enabled&amp;quot;: true,  # 下载队列开关
&amp;quot;download-queue-size&amp;quot;: 5, # 下载队列数量
&amp;quot;encryption&amp;quot;: 1, # 加密。指定节点的加密模式，默认 1。0 表示关闭 , 0&amp;#x3D; 不加密，1&amp;#x3D; 优先加密，2&amp;#x3D; 必须加密
&amp;quot;lazy-bitfield-enabled&amp;quot;: true, # 默认为 true，设置为 true 时可以避免某些 ISP 通过查询完整位段来屏蔽 BT，从而破解部分 ISP 对 BT 的封杀，当然不一定完全有效
&amp;quot;idle-seeding-limit&amp;quot;: 30,
&amp;quot;idle-seeding-limit-enabled&amp;quot;: false,
