static int __DevStatus &#x3D; 0

void Dev_Init(void)
&#123;
	&#x2F;&#x2F; Print debug information
	printf(&quot;Dev Initialize OK!\r\n&quot;);
&#125;

int Dev_PrintInt(int number)
&#123;
	printf(&quot;Print IntType number: %d\r\n&quot;, number);
&#125;

int Dev_StatusCheck(void)
&#123;
	return 	(__DevStatus);
&#125;

