                        &lt;%- gallery.name %&gt;
                    &lt;/p&gt;
                &lt;/a&gt;
            &lt;/div&gt;
            &lt;% } %&gt;
        &lt;/div&gt;
        &lt;% } %&gt;
    &lt;/div&gt;
&lt;/main&gt;
