    imageStr += "&lt;a href=\"" + galleryImageStr + photo + "\"" +
            "     class=\"photo-item\" rel=\"example_group\"" +
            "     data-fancybox=\"images\"&gt;" +
            "      &lt;img src=\"" + galleryImageStr + photo + "\"" +
            "       alt=" + photo + "&gt;\n" +
            "    &lt;/a&gt;"

}
